+ final subject = _subjects[subjectIndex];
+ final completedLessons = (subject['completedLessons'] as int) + 1;
+ final totalLessons = subject['totalLessons'] as int;
+ final progress = ((completedLessons / totalLessons) * 100).round();
+ 
+ _subjects[subjectIndex]['completedLessons'] = completedLessons;
+ _subjects[subjectIndex]['progress'] = progress;
+ _subjects[subjectIndex]['lastStudied'] = DateTime.now();
