+ GoRoute(
+ path: '/auth/login',
+ name: 'login',
+ builder: (context, state) => const LoginPage(),
+ ),
+ GoRoute(
+ path: '/auth/register',
+ name: 'register',
+ builder: (context, state) => const RegisterPage(),
