+ name: 'exam-detail',
+ builder: (context, state) {
+ final examId = state.pathParameters['examId']!;
+ return ExamDetailPage(examId: examId);
+ },
+ routes: [
+ GoRoute(
+ path: '/take',
+ name: 'exam-taking',
+ builder: (context, state) {
+ final examId = state.pathParameters['examId']!;
+ final settings = state.extra as Map<String, dynamic>?;
+ return ExamTakingPage(
+ examId: examId,
+ settings: settings ?? {},
+ );
+ },
+ ),
+ GoRoute(
+ path: '/results',
+ name: 'exam-results',
+ builder: (context, state) {
+ final examId = state.pathParameters['examId']!;
+ final results = state.extra as Map<String, dynamic>?;
+ return ExamResultsPage(
+ examId: examId,
+ results: results ?? {},
+ );
+ },
+ ),
+ ],
+ ),
+ ],
+ ),
+ 
+ // الدراسة
+ GoRoute(
+ path: '/study',
+ name: 'study',
