+class AuthProvider extends ChangeNotifier {
+ final FirebaseAuth _auth = FirebaseAuth.instance;
+ final FirebaseFirestore _firestore = FirebaseFirestore.instance;
+
+ UserModel? _currentUser;
+ bool _isLoading = false;
+ String? _errorMessage;
+
+ // Getters
+ UserModel? get currentUser => _currentUser;
+ bool get isLoading => _isLoading;
+ String? get errorMessage => _errorMessage;
+ bool get isAuthenticated => _currentUser != null;
+
+ AuthProvider() {
+ _initializeAuth();
+ }
+
+ // تهيئة المصادقة
+ void _initializeAuth() {
+ _auth.authStateChanges().listen((User? user) async {
+ if (user != null) {
+ await _loadUserProfile(user);
+ } else {
+ _currentUser = null;
+ notifyListeners();
+ }
+ });
+ }
+
+ // تحميل ملف المستخدم
+ Future<void> _loadUserProfile(User user) async {
+ try {
+ _setLoading(true);
+ 
+ // تحديث وقت آخر تسجيل دخول
+ await _updateLastLogin(user.uid);
+ 
+ // تحميل البيانات من Firestore
+ final userDoc = await _firestore.collection('users').doc(user.uid).get();
+ 
+ if (userDoc.exists) {
+ _currentUser = UserModel.fromFirestore(userDoc);
+ } else {
+ // إنشاء ملف جديد إذا لم يكن موجوداً
+ _currentUser = UserModel.fromFirebaseUser(user);
+ await _createUserProfile(_currentUser!);
+ }
+ 
+ _clearError();
+ } catch (e) {
+ _setError('فشل في تحميل بيانات المستخدم: ${e.toString()}');
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // تسجيل الدخول بالبريد الإلكتروني
+ Future<bool> signInWithEmailAndPassword(String email, String password) async {
+ try {
+ _setLoading(true);
+ _clearError();
+
+ final credential = await _auth.signInWithEmailAndPassword(
+ email: email,
+ password: password,
+ );
+
+ return credential.user != null;
+ } on FirebaseAuthException catch (e) {
+ _setError(_getAuthErrorMessage(e.code));
+ return false;
+ } catch (e) {
+ _setError('حدث خطأ غير متوقع: ${e.toString()}');
+ return false;
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // إنشاء حساب جديد
+ Future<bool> createUserWithEmailAndPassword(String email, String password, String displayName) async {
+ try {
+ _setLoading(true);
+ _clearError();
+
+ final credential = await _auth.createUserWithEmailAndPassword(
+ email: email,
+ password: password,
+ );
+
+ if (credential.user != null) {
+ // تحديث اسم المستخدم
+ await credential.user!.updateDisplayName(displayName);
+ 
+ // إرسال رسالة تأكيد البريد الإلكتروني
+ await credential.user!.sendEmailVerification();
+ 
+ return true;
+ }
+ return false;
+ } on FirebaseAuthException catch (e) {
+ _setError(_getAuthErrorMessage(e.code));
+ return false;
+ } catch (e) {
+ _setError('حدث خطأ غير متوقع: ${e.toString()}');
+ return false;
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // إعادة تعيين كلمة المرور
+ Future<bool> sendPasswordResetEmail(String email) async {
+ try {
+ _setLoading(true);
+ _clearError();
+
+ await _auth.sendPasswordResetEmail(email: email);
+ return true;
+ } on FirebaseAuthException catch (e) {
+ _setError(_getAuthErrorMessage(e.code));
+ return false;
+ } catch (e) {
+ _setError('حدث خطأ غير متوقع: ${e.toString()}');
+ return false;
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // تسجيل الخروج
+ Future<void> signOut() async {
+ try {
+ _setLoading(true);
+ await _auth.signOut();
+ _currentUser = null;
+ } catch (e) {
+ _setError('فشل في تسجيل الخروج: ${e.toString()}');
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // إنشاء ملف المستخدم في Firestore
+ Future<void> _createUserProfile(UserModel user) async {
+ await _firestore.collection('users').doc(user.id).set(user.toFirestore());
+ }
+
+ // تحديث وقت آخر تسجيل دخول
+ Future<void> _updateLastLogin(String userId) async {
+ await _firestore.collection('users').doc(userId).update({
+ 'lastLoginAt': Timestamp.now(),
+ });
+ }
+
+ // تحديث ملف المستخدم
+ Future<bool> updateUserProfile(Map<String, dynamic> updates) async {
+ if (_currentUser == null) return false;
+
+ try {
+ _setLoading(true);
+ 
+ await _firestore.collection('users').doc(_currentUser!.id).update(updates);
+ 
+ // إعادة تحميل البيانات
+ final userDoc = await _firestore.collection('users').doc(_currentUser!.id).get();
+ _currentUser = UserModel.fromFirestore(userDoc);
+ 
+ _clearError();
+ return true;
+ } catch (e) {
+ _setError('فشل في تحديث الملف الشخصي: ${e.toString()}');
+ return false;
+ } finally {
+ _setLoading(false);
+ }
+ }
+
+ // Helper methods
+ void _setLoading(bool loading) {
+ _isLoading = loading;
+ notifyListeners();
+ }
+
+ void _setError(String error) {
+ _errorMessage = error;
+ notifyListeners();
+ }
+
+ void _clearError() {
+ _errorMessage = null;
+ notifyListeners();
+ }
+
+ // ترجمة رسائل الخطأ
+ String _getAuthErrorMessage(String errorCode) {
+ switch (errorCode) {
