+ BoxShadow(
+ color: Colors.black.withOpacity(0.05),
+ blurRadius: elevation ?? 4,
+ offset: const Offset(0, 2),
+ ),
+ ],
+ ),
+ child: Padding(
+ padding: padding ?? const EdgeInsets.all(16),
+ child: child,
+ ),
+ );
+
+ if (onTap != null) {
+ return GestureDetector(
+ onTap: onTap,
+ child: card,
+ );
+ }
