+class _ExamTakingPageState extends State<ExamTakingPage> {
+ late List<Map<String, dynamic>> questions;
+ late Map<int, String> answers;
+ late Timer timer;
+ late int timeRemaining; // بالثواني
+ int currentQuestionIndex = 0;
+ bool isLoading = true;
+ bool isSubmitting = false;
+
+ @override
+ void initState() {
+ super.initState();
+ _loadExam();
+ }
+
+ @override
+ void dispose() {
+ timer.cancel();
+ super.dispose();
+ }
+
+ Future<void> _loadExam() async {
+ // محاكاة تحميل البيانات
+ await Future.delayed(const Duration(seconds: 1));
+ 
+ setState(() {
+ questions = _getExamQuestions();
+ answers = {};
+ timeRemaining = 30 * 60; // 30 دقيقة
+ isLoading = false;
+ });
+ 
+ _startTimer();
+ }
+
+ void _startTimer() {
+ timer = Timer.periodic(const Duration(seconds: 1), (timer) {
+ if (timeRemaining > 0) {
+ setState(() {
+ timeRemaining--;
+ });
+ } else {
+ _submitExam();
+ }
+ });
+ }
+
+ @override
+ Widget build(BuildContext context) {
+ if (isLoading) {
+ return const Scaffold(
+ body: Center(
+ child: CircularProgressIndicator(),
+ ),
+ );
